import React from 'react';
import "./AddReview.css";
import Header from './Header';
import reviews from '../data/coursereviews';
import { useState } from 'react';

function AddReview() {
    const [name, setName] = useState("");
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [rev, setRev] = useState(reviews);
  return (
    <div className='body' >
    <Header/>

    <form>      
  <input name="name"
   onChange={(e) => setName(e.target.value)}
   type="text" class="feedback-input" placeholder="Name" />   
  <input name="email" type="text" 
   onChange={(e) => setTitle(e.target.value)}
  class="feedback-input" placeholder="Email" />
  <textarea name="text"
   onChange={(e) => setContent(e.target.value)}
   class="feedback-input" placeholder="Comment"></textarea>
  <input type="submit" 
  onClick={()=>{

         setRev([...rev,
         { name: {name},
           course_title: {title},
           content: {content},

         }])

        }}
  value="SUBMIT"/>
</form>


    </div>
  )
}

export default AddReview
