import React from 'react'
import Header from './Header';
import { useLocation } from 'react-router-dom';
import "./CoursePage.css";
import initialDetails from "../data/courseinfo";
import ReviewCard from './ReviewCard';
import { Margin } from '@mui/icons-material';
import {reviews} from '../data/coursereviews';

function CoursePage() {
    const {state} = useLocation();
    const {name,id} = state;

  return (

<div className='course_info'>
<Header/>
<h1 >
    {initialDetails[id-1].course_title} 
    </h1>
    <h3>
      {name}
    </h3>
    <h3>
        Contents:
    </h3>
    <p>
        {initialDetails[id-1].content}
    </p>
    <h3>
        Course Instructors:
    </h3>
    <h2>
    Nilesh Bhadve (2020-21)
    </h2>
    <h2>
    Kantesh Balani(2019-20)
    </h2>
    <h2>
    Somnath Bhowmick(2018-19)
    </h2>
    <button className='button-85'>
        Assignments
    </button>
    <button className='button-85'>
        PYQ's
    </button>
    <button className='button-85'>
        Textbooks
    </button>
    <hr></hr>
    <h3 className='txt'>
        Course Reviews-
    </h3>
    <div className='reviewcard'>

  {
    reviews.map((review) =><ReviewCard name={review.name} content={review.content } year={review.year} course_title={review.course_title}/>)}






    </div>



</div>



  )
}

export default CoursePage
