import React, { useState } from "react";
import {
  AppBar,
  Tab,
  Tabs,
  Toolbar,
  Typography,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import DrawerComp from "./Drawer";
import { useNavigate} from 'react-router-dom';

const Header = () => {
  const navigate=useNavigate();
  const [goToCourse,setGoToCourse]=React.useState(false);
  const [goToCourse1,setGoToCourse1]=React.useState(false);

  const [value, setValue] = useState();
  const theme = useTheme();
  console.log(theme);
  const isMatch = useMediaQuery(theme.breakpoints.down("md"));
  console.log(isMatch);
  if(goToCourse) {
    return navigate('/addreview')
  }
  if(goToCourse1) {
    return navigate('/')
  }

  return (
    <React.Fragment>
      <AppBar sx={{ background: "rgb(30,30,50)" }}>
        <Toolbar>

          {isMatch ? (
            <>
              <Typography sx={{ fontSize: "2rem", paddingLeft: "10%" }}>
                Course Assist
              </Typography>
              <DrawerComp />
            </>
          ) : (
            <>
              <Tabs
                sx={{ marginLeft: "auto" }}
                indicatorColor="secondary"
                textColor="yellow"
                value={value}
                onChange={(e, value) => setValue(value)}
              >
                 <Typography sx={{ fontSize: "2rem" }}>
                Course Assist
              </Typography>
                <Tab onClick={()=>setGoToCourse1(true)} label={<span style={{ color: '#ffd52d' }}>Home</span>} />
                <Tab onClick={()=>setGoToCourse(true)} label={<span style={{ color: '#ffd52d' }}>Add Review</span>} />
                <Tab label={<span style={{ color: '#ffd52d' }}>Contact</span>} />
                <Tab label={<span style={{ color: '#ffd52d' }}>About us</span>} />
              </Tabs>

            </>
          )}
        </Toolbar>
      </AppBar>
    </React.Fragment>
  );
};

export default Header;
