import React from 'react';
import "./AddReview.css";
import Header from './Header';
import reviews from '../data/coursereviews';
import { useState } from 'react';

function AddReview() {
    const [name, setName] = useState("");
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [rev, setRev] = useState(reviews);
  return (
    <div className='body' >
    <Header/>

    <div className="wrapper">
    <form className="form">
      <h3 className="pageTitle">
      Course Review </h3>
      <h4 className="secondaryTitle">
      Please fill this form for course review</h4>
      <input type="text" 
       onChange={(e) => setName(e.target.value)}
      className="name" placeholder="Student Name" />
      <input type="text"
      onChange={(e) => setTitle(e.target.value)}
       className="email" placeholder="Course Title"/>
      <textarea className="message"
      onChange={(e) => setContent(e.target.value)}

       placeholder="Review"></textarea>
         <button onClick={()=>{

          setRev([...rev,
          { name: {name},
            course_title: {title},
            content: {content}

          }])

         }} className="submit" >Submit</button>
    </form>
  </div>


    </div>
  )
}

export default AddReview
