import React from 'react'
import Header from './Header';
import { useLocation } from 'react-router-dom';
import "./CoursePage.css";
import initialDetails from "../data/courseinfo";
import ReviewCard from './ReviewCard';
import { Margin } from '@mui/icons-material';
import {reviews} from '../data/coursereviews';
import { useState,useEffect } from 'react';
import Axios from 'axios';

function CoursePage() {
    const {state} = useLocation();
    const {name,id} = state;
    const [reviewList,setReviewList]=useState([]);

    useEffect(() => {
        Axios.get("http://localhost:3001/api/get").then((response) =>{
            setReviewList(response.data);
        })
    })

  return (

<div className='course_info'>
<Header/>
<h1 >
    {initialDetails[id-1].course_title} 
    </h1>
    <h3>
      {name}
    </h3>
    <h3>
        Contents:
    </h3>
    <p>
        {initialDetails[id-1].content}
    </p>
    <h3>
        Course Instructors:
    </h3>
    <h2>
    Nilesh Bhadve (2020-21)
    </h2>
    <h2>
    Kantesh Balani(2019-20)
    </h2>
    <h2>
    Somnath Bhowmick(2018-19)
    </h2>
    <button className='button-85'>
        Assignments
    </button>
    <button className='button-85'>
        PYQ's
    </button>
    <button className='button-85'>
        Textbooks
    </button>
    <hr></hr>
    <h3 className='txt'>
        Course Reviews-
    </h3>
    <div className='reviewcard'>

  {
    reviewList.map((review) =><ReviewCard name={review.student_name} content={review.description } year="2022" course_title={review.title}/>)}






    </div>



</div>



  )
}

export default CoursePage
