import React from 'react';
import "./Card.css";
import { Navigate} from 'react-router-dom';
function Card({person}) {
  const [goToCourse,setGoToCourse]=React.useState(false);
  if(goToCourse) {
    return <Navigate to={"/coursepage"} state={person.name} />;
  }

	return(
		<div className="card">
  <div className="card-details">
    <p className="text-title">{person.course_title}</p>
    <p className="text-body">{person.name}</p>
  </div>
  <button className="card-button" onClick={()=>{
   setGoToCourse(true);
  }}>More info</button>

</div>
	);
}

export default Card;
